    line-height: 1.5em !important; /*wprm_main_line_height type=font_size*/
}
.wprm-recipe-template-template-1 li {
    margin: 0 0 12px 15px !important;
    padding: 0 !important;
}
.wprm-recipe-template-template-1 li.wprm-recipe-ingredient {
  	margin-left: 0 !important;
}
.wprm-recipe-ingredient input[type=checkbox]:checked ~ span {
  text-decoration: line-through;
  opacity: 0.7;
}
.wprm-recipe-ingredient input[type=checkbox] {
  margin: 0;
  margin-right: 8px;
  border-radius: 2px;
  border-color: #666666;
  position: relative;
  box-shadow: none;
  width: 19px;
  height: 19px;
  margin-top: -4px;
  border-width: 2px;
}
.wprm-recipe-ingredient input[type=checkbox]::before {
    position: absolute;
    left: 0;
    right: 0;
    top: 0;
    bottom: 0;
    content: "";
  	width: 100%;
}
.wprm-recipe-ingredient input[type=checkbox]:checked {
	border-color: #098CE9;
}
.wprm-recipe-ingredient input[type=checkbox]:checked:before {
  background-image: url("data:image/svg+xml,%3Csvg width='11' height='9' viewBox='0 0 11 9' fill='none' xmlns='http://www.w3.org/2000/svg'%3E%3Cpath d='M9.60306 0.383759L4.32171 6.60194L1.38362 3.67467C1.05917 3.3474 0.55447 3.38376 0.230018 3.67467C-0.0944332 4.00194 -0.058383 4.51103 0.230018 4.8383L3.52861 8.09285C3.74491 8.31103 4.03331 8.42012 4.32171 8.42012C4.61011 8.42012 4.89851 8.31103 5.11481 8.09285L10.7567 1.58376C11.0811 1.25649 11.0811 0.747395 10.7567 0.420122C10.4322 0.0928495 9.92751 0.09285 9.60306 0.383759Z' fill='%23098CE9'/%3E%3C/svg%3E%0A") !important;
  background-position: center !important;
  background-repeat: no-repeat !important;
}
.rtl .wprm-recipe-template-template-1 li {
    margin: 0 15px 0 0 !important;
}
.wprm-recipe-template-template-1 ol, .wprm-recipe-template-template-1 ul {
    margin: 0 !important;
    padding: 0 !important;
}
.wprm-recipe-template-template-1 br {
    display: none;
}
.wprm-recipe-template-template-1 .wprm-recipe-name,
.wprm-recipe-template-template-1 .wprm-recipe-header {
    font-family: inherit; /*wprm_header_font_family type=font*/
    color: #212121; /*wprm_header_text type=color*/
    line-height: 1.3em; /*wprm_header_line_height type=font_size*/	
    position: relative;
}
.wprm-recipe-template-template-1 .wprm-recipe-header {
  margin-bottom: 30px !important;
  font-size: 24px;
  text-transform: capitalize !important;
  font-weight: 700;
  font-family: var(--thim-font-title-font-family);
}

.wprm-recipe-ingredients-container {
  border: 1px solid #E2E0DB;
  padding: 30px;
  border-radius: 8px 8px 0 0;
  margin-bottom: 0;
  border-bottom: 0;
}

.wprm-recipe-image img {
  width: 100%;
  margin-bottom: 30px;
  border-radius: 0 0 8px 8px;
}

.wprm-recipe-template-template-1 h1,
.wprm-recipe-template-template-1 h2,
.wprm-recipe-template-template-1 h3,
.wprm-recipe-template-template-1 h4,
.wprm-recipe-template-template-1 h5,
.wprm-recipe-template-template-1 h6 {
    font-family: inherit; /*wprm_header_font_family type=font*/
    color: #212121; /*wprm_header_text type=color*/
    line-height: 1.3em; /*wprm_header_line_height type=font_size*/
    margin: 0 !important;
    padding: 0 !important;
}

.wprm-recipe-template-template-1 h1 {
    font-size: 2em; /*wprm_h1_size type=font_size*/
}
.wprm-recipe-template-template-1 h2 {
    font-size: 1.8em; /*wprm_h2_size type=font_size*/
}
.wprm-recipe-template-template-1 h3 {
    font-size: 1.2em; /*wprm_h3_size type=font_size*/
}
.wprm-recipe-template-template-1 h4 {
    font-size: 1em; /*wprm_h4_size type=font_size*/
}
.wprm-recipe-template-template-1 h5 {
    font-size: 1em; /*wprm_h5_size type=font_size*/
}
.wprm-recipe-template-template-1 h6 {
    font-size: 1em; /*wprm_h6_size type=font_size*/
}.wprm-recipe-template-template-1 {
    font-size: 1em; /*wprm_main_font_size type=font_size*/
	border-style: solid; /*wprm_border_style type=border*/
	border-width: 1px; /*wprm_border_width type=size*/
	border-color: #ffffff; /*wprm_border type=color*/
    background-color: #ffffff; /*wprm_background type=color*/
    max-width: 950px; /*wprm_max_width type=size*/
  	border-radius: 8px;
}
.wprm-recipe-template-template-1 a {
    color: #5A822B; /*wprm_link type=color*/
}
.wprm-recipe-template-template-1 .wprm-recipe-name {
    line-height: 1.3em;
    font-weight: bold;
}
.wprm-recipe-servings-toggle {
  display: inline-flex;
  border: none;
  align-items: center;
  position: absolute;
  right: 0;
  left: auto;
  top: 0;
}
.wprm-recipe-servings-toggle h5 {
  margin-right: 12px !important;
}
.wprm-recipe-servings-toggle button.wprm-toggle {
  border: 1px solid #DDDDDD;
  width: 36px;
  height: 36px;
  font-weight: 400;
  padding: 0;
  border-right: 0;
}
.wprm-recipe-servings-toggle button.wprm-toggle:last-child {
   border-right: 1px solid #DDDDDD;
  border-radius: 0 8px 8px 0;
}
.wprm-recipe-servings-toggle button.wprm-toggle:nth-child(2){
  border-radius: 8px 0 0 8px;
}
.wprm-recipe-servings-toggle button.wprm-toggle.wprm-toggle-active {
  background: #098CE9;
  border-color: #098CE9;
  color: #fff;
}
.wprm-recipe-template-template-1 .wprm-template-chic-buttons {
	clear: both;
    font-size: 0.9em;
    text-align: center;
}
.wprm-recipe-template-template-1 .wprm-template-chic-buttons .wprm-recipe-icon {
    margin-right: 5px;
}
.wprm-recipe-template-template-1 .wprm-nutrition-label-container {
	font-size: 0.9em;
}
.wprm-recipe-template-template-1 .wprm-call-to-action {
	border-radius: 3px;
}
.wprm-recipe-video-container iframe {
  	border-radius: 8px;
}
.comment-form-wprm-rating label {
  display: none;
}
.content-comment .wprm-comment-rating {
  filter: sepia(1) saturate(1000%) hue-rotate(370deg) brightness(3);
  margin: 0 0 10px 0; 
}
@media (max-width: 1024px){
  .wprm-container-header {
    gap: 20px;
    flex-direction: column;
  }
  .wprm-container-header .wprm-recipe-image {
      max-width: 200px;
      min-width: auto;
  }
  .wprm-recipe-times-container, .wprm-recipe-custom-container {
      display: flex;
      column-gap: 20px;
      flex-wrap: wrap;
  }
}
@media (max-width: 500px){
  .wprm-recipe-template-template-1 .wprm-recipe-ingredients-header {
       padding-top: 60px !important;
  }
  .wprm-recipe-servings-toggle {
    left: 0;
    right: auto;
  }
}

