.wprm-recipe-template-template-button-1 {
    font-family: inherit; /*wprm_font_family type=font*/
    font-size: 14px; /*wprm_font_size type=font_size*/
    text-align: left; /*wprm_text_align type=align*/
    margin-top: 0px; /*wprm_margin_top type=size*/
    margin-bottom: 30px; /*wprm_margin_bottom type=size*/
    display: flex;
    row-gap: 12px;
    flex-wrap: wrap;
}
.wprm-recipe-template-template-button-1 a  {
    margin: 0 12px 0 0 !important;
}

.wprm-recipe-template-template-button-1 a:first-child {
    margin-left: 0;
}
.wprm-recipe-template-template-button-1 a:last-child {
    margin-right: 0 !important;
}
.wprm-recipe-snippet.wprm-recipe-template-template-button-1 .wprm-recipe-link-inline-button {
  line-height:  1.5em;
  font-weight: 500;
  font-size: 16px;
